*{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}

body {

}

.menubar {
    background-color: rgba(0,0,0,0.38);
    background-color: var(--middleGrayColor);
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    color: rgba(0,0,0,0.2);
    color: var(--weakGrayColor);

}

.menubar .menubar__ul {
    display: grid;
    grid-auto-rows: 73px;
    grid-template-columns: repeat(auto-fill, minmax(135px, 1fr));
    grid-auto-flow: column;
    padding-left: 50px;
}

.menubar .menubar__ul li{
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%
}

.menubar .menubar__ul li:hover {
        background-color: rgba(0,0,0,0.4);
        background-color: var(--deeperGrayColor);
    }


.menubar .menubar__ul li a{
    font-weight: 600px;
    font-size: 18px;
}


li, a {
    list-style: none;
    color: white;
    text-decoration: none;
}

.menubar li ul {
    background-color: rgba(0,0,0,0.38);
    background-color: var(--middleGrayColor);
    display: none;
    position: absolute;
    left: 189px;
    top: 73px;
    width: 260px;
    height: 260px;
}

.menubar li:hover ul{
    display: flex;
    flex-direction: column;
}
